<script lang="ts">
    import Icon from '@iconify/svelte';
    import {createEventDispatcher} from 'svelte';

    export let players = [];
    export let self: { username: string; role: string } = {};
    const dispatch = createEventDispatcher();

    let images = {
        name: 'data:image/jpeg;base64,',
        other:
            'data:image/png;base64,',
        test: 'data:image/jpeg;base64,',
        fail: 'data:image/jpeg;base64,'
    };

    function randomChoice(arr) {
        return arr[Math.floor(arr.length * Math.random())];
    }

    export let maxPlayers = 0;
</script>

<div class="flex flex-col h-full max-h-48">
    <h3 class="text-slate-400 text-xs font-bold">PLAYERS - {players.length}</h3>
    <div class="flex-1 flex flex-col w-full overflow-y-auto scrollbar-hidden p-1">
        {#each players as player}
            <div class="rounded text-slate-100 pl-1 flex gap-2 items-center p-1">
                <div>
                    <img
                            src={randomChoice(Object.values(images))}
                            class="rounded-full bg-yellow-500 h-8 w-8"
                    />
                </div>
                <div class="flex-1 text-lg font-bold flex items-center justify-between gap-1">
                    <p class="truncate inline-flex items-center gap-2">
                        {player.username}
                        {#if self.username === player.username}<span class="text-xs text-slate-400">(YOU)</span
                        >{/if}
                    </p>
                    {#if player.role === 'host'}
                        <div class="text-yellow-400 block">
                            <Icon icon="ic:round-star"/>
                        </div>
                    {/if}

                    {#if self.role === 'host' & self.username !== player.username}
                        <button
                                class="text-slate-600 hover:text-red-500 duration-200"
                                on:click={() => {
								dispatch('kick', player);
							}}
                        >
                            <Icon icon="mdi:trash-can"/>
                        </button>
                    {/if}
                </div>
            </div>
        {/each}
    </div>
</div>
